import React, {Component} from 'react';
import axios from 'axios';

class ContactForm extends Component{
    constructor(){
      super()
      this.state = {
        name: '',
        email: '',
        message: ''
      }
      this.handleChange = this.handleChange.bind(this);
    }

    handleChange = e => {
      this.setState({[e.target.name]: e.target.value})
    }


    async handleSubmit(e){
        e.preventDefault();
        const {name, email, message} = this.state;
        console.log('this is leaving the route')
        const form = await axios.post('/api/form', {
          name: name,
          email: email,
          message: message
        })
    }

    render(){
        return(
            <div className="col-sm-4 offset-sm-4">
                <form id="contact-form" onSubmit={this.handleSubmit.bind(this)} method="POST">
                    <div className="form-group">
                        <label for="name">Name</label>
                        <input type="text" className="form-control" id="name" onChange={this.handleChange}/>
                    </div>
                    <div className="form-group">
                        <label for="exampleInputEmail1">Email address</label>
                        <input type="email" className="form-control" id="email" aria-describedby="emailHelp"  onChange={this.handleChange} />
                    </div>
                    <div className="form-group">
                        <label for="message">Message</label>
                        <textarea className="form-control" rows="5" id="message"  onChange={this.handleChange}></textarea>
                    </div>
                    <button type="submit" className="btn btn-primary">Submit</button>
                </form>
            </div>
        )
    }
}

export default ContactForm;
